import axios from 'axios';
export async function getSpec(url) {
    let spec;
    try {
        const { data } = await axios.get(url);
        spec = data;
    }
    catch (error) {
        console.error('Failed to fetch OpenAPI spec:', error?.response.status, error?.response.statusText);
        process.exit(1);
    }
    return spec;
}
